using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace AdsPortal.Logic
{
    public class AdManager
    {
        private int CurrentId;
        private List<Ad> Ads;

        public AdManager()
        {
            CurrentId = 100;
            Ads = new List<Ad>();
        }

        public List<Ad> GetByCategory(int categoryId)
        {
            var items = Ads.FindAll(i => i.CategoryId == categoryId);

            return items;
        }

        public Ad Create(Ad item)
        {
            item.CreatedOn = DateTime.Now;
            item.Id = CurrentId;

            Ads.Add(item);
            CurrentId++;

            return item;
        }

        public void Update(Ad item)
        {
            var currentItem = Ads.Find(i => i.Id == item.Id);
            currentItem.Photo = item.Photo;
            currentItem.CategoryId = item.CategoryId;
            currentItem.Description = item.Description;
            currentItem.Price = item.Price;
            currentItem.Title = item.Title;
            currentItem.Email = item.Email;
            currentItem.Location = item.Location;
            currentItem.Telephone = item.Telephone;
        }

        public void Delete(int id)
        {
            var item = Ads.Find(i => i.Id == id);
            Ads.Remove(item);
        }

        public Ad Get(int id)
        {
            var item = Ads.Find(i => i.Id == id);
            return item;
        }

